var plaguetotal=0;
var plagueend=300;
var totalresource=0;
var totalresearch=0;
var actualresearch=0;
var upperres=0;
var upperend=0;
var colorcheck=0;
var warningcheck=0;
//check vars for research
var check1=0;
var check2=0;
var check3=0;
var check4=0;
//vars for resources
var quarantine=0;
var medicine=0;
var barrier=0;
var research=0;
//vars for research types
var anti=0;
var vaccine=0;
var aegis=0;
var interferon=0;
//vars for initial slider positions . . . don't hate me for color coding
var red=0;
var blue=0;
var green=0;
var purple=0;
//vars for Game Engine
var turnNumber=1;
var gameStage=0;
