var infectionSites=Math.floor((Math.random()*(5-2))+2);
//incrementing vars to hold the increasing research total from turn to turn
var antiInc=0;
var vaccineInc=0;
var aegisInc=0;
var interferonInc=0;
//random vars for research completion
//these values determine when research is finished
var antiviralFinished=Math.floor((Math.random()*(2800-2401))+2400);
var interferonFinished=Math.floor((Math.random()*(2800-2401))+2400);
var vaccineFinished=Math.floor((Math.random()*(2800-2401))+2400);
//one needs to be easier than the others

var determine=Math.floor((Math.random()*3)+1);

switch (determine) {
case 1:
	antiviralFinished=1101;
	break;
case 2:
	interferonFinished=1101;
	break;
default:
	vaccineFinished=1101;
	break;
}
//we use these for the information pane
var treatthis=0;
if (vaccineFinished>antiviralFinished&&antiviralFinished<interferonFinished)
{
	treatthis=1;
}
else if (antiviralFinished>vaccineFinished&&vaccineFinished<interferonFinished)
{
	treatthis=2;
}
else if (antiviralFinished>interferonFinished&&interferonFinished<vaccineFinished)
{
	treatthis=3;
}


//the aegis vaccine always takes less resources to buy than real medicine
var aegisFinished=Math.floor((Math.random()*(1400-1101))+1100);
//random vars for research effectiveness
//when fully researched and distributed all of these will cure the Zombie plague
//but these vars determine how rapidly they will work as multiplied by the distribute medicine strategy
var antiviralEffect=Math.floor((Math.random()*(15-13))+12);
var interferonEffect=Math.floor((Math.random()*(15-13))+12);
var vaccineEffect=Math.floor((Math.random()*(15-13))+12);
//random vars for strategy effectiveness
var quarantineEffect=Math.floor((Math.random()*(15-12))+11);
var barrierEffect=Math.floor((Math.random()*(15-12))+11);

//let's keep them from being equal
if (quarantineEffect==barrierEffect)
	{quarantineEffect--;}
//we use these for the information pane
var quarthis=1;
if (quarantineEffect>barrierEffect)
{
	quarthis=1;
}
else if (quarantineEffect<barrierEffect)
{
	quarthis=2;
}
//for AJAX
//var obj=new Object();
//the medicine strategy is a base multiplier. Until the proper research reveals effective medicines there is only a placebo effect
//or 1. After something is revealed through research that value will be multiplied into medicineEffect to create a new value
var medicineEffect=1;
//the aegis drug option is a special case. It is never effective on more than a handful of people
//+ some temporary placebo calming effect on population. This var determines how effective that is
//although it is pretty much always a dead end.
var aegisEffect=Math.floor((Math.random()*6)+3);

//set map image 

imageit=document.createElement("IMG");
imageit.id="imageit";
imageit.src="data:image/png;base64,";

//Cut scene function
