//this subroutine checks each turn to see the extent of the infection
//it works by counting the number of red pixels in the image
//yeah, it's pretty awesome
function checkTheColor(ctx, turnchange)
{
	
	var xmax=630;
	var ymax=400;
	//var totallo=1008000; //I could have the app calculate this, but why add overhead?
	var x=0;
	var y=0;
	var start=0;
//extract the image pixels
//my first version looped through each pixel in the canvas; stupid and inefficient
//worked particularly poorly on Chrome
//this creates an array of all the pixels in the image at once
var datait = ctx.getImageData(x, y, xmax, ymax);
var totallo=datait.data.length;
//loop through pixel array looking for red enough pixels
while (start<totallo)
{
if (datait.data[start]>170&&datait.data[start]<256)
{
colorcheck++;
}
//pixels are subdivied into 4 values (red first). I step by four to only check red values
start=start+4;
}

if (colorcheck>223392)
{
	lose_game();
}
else if (colorcheck<119350)
{
	win();
}
else if ((colorcheck>133871)&&(warningcheck==0))
{
	warning();
	warningcheck++;
}
var percentage=(colorcheck*100)/223392;

if (turnchange>=0)
{
$("#mapspeak").html("<p>Attention: The radius of the infected zones has increased by "+(Math.round(turnchange*2))+" miles. You are at "+Math.round(percentage)+"% of an out of control infection.");
}
else
{
$("#mapspeak").html("<p>Attention: The radius of the infected zones has decreased by "+Math.round(Math.abs(turnchange*2))+" miles. You are at "+Math.round(percentage)+"% of an out of control infection.");
}

colorcheck=0;
infoPane();

}


