function changeZoneSize(turnchange)
{
var c=document.getElementById("zombiemap");
var ctx=c.getContext("2d");
//save matrix and clear the canvas
ctx.save();
ctx.clearRect(0, 0, c.width, c.height);
//restore matrix
ctx.restore();
ctx.drawImage(imageit,10,10);
i=0;

while(i<(infectionSites))
{
//using objects for site centers . . . much cleaner than nesting arrays 
var holding="infectionSite"+i;
var nextholding=myGameScope[holding];
if (nextholding.radi<5)
{
turnchange=turnchange-quarantine;
turnchange=turnchange-barrier;	
}
if (turnchange<0&&medicineEffect==1)
	{turnchange=0;}

nextholding.radi=nextholding.radi+turnchange;

//alert(nextholding.radi+"--"+turnchange+"----"+parseFloat(turnchange)+parseFloat(nextholding.radi)+"----"+newrad);
if (nextholding.radi>0)
{
ctx.beginPath();
ctx.arc(nextholding.x,nextholding.y,nextholding.radi,0,2*Math.PI);
ctx.fillStyle = "rgba(255, 0, 0, 0.7)";
ctx.fill();
}
i++; 


}
checkTheColor(ctx, turnchange);
}

function checkMap()
{
//redisplay the map if not shown
if ($("#zombiecontain").is(":hidden"))
{
    $("#controlpanel").hide();
    $("#infopanel").hide();
  
    $("#zombiecontain").show("slow", function(){turnEngine();});
}
else
{
//move to the main event
turnEngine();
}
}
//the root game engine function
function turnEngine()
{
quarantine = $( "#red" ).slider( "value" );
medicine = $( "#green" ).slider( "value" );
barrier = $( "#blue" ).slider( "value" );
research = $( "#purple" ).slider( "value" );
//modify values
quarantine*=0.01;
medicine*=0.01;
barrier*=0.01;
research*=0.01;
//increment research
antiInc+=(anti*research);
vaccineInc+=(vaccine*research);
interferonInc+=(interferon*research);
aegisInc+=(aegis*research);
//see if any research has been finished
//not using elses because all finished items must change medicineEffect
if (antiInc>=antiviralFinished&&check1<1)
{
var pass="anti-viral";
//alert(antiviralFinished+"-----"+antiviralEffect);
researchComplete(pass);
medicineEffect=medicineEffect*(antiviralEffect);
check1++;
}
//now I change the medicineEffect operator to += to catch all modifiers
if (vaccineInc>=vaccineFinished&&check2<1)
{
var pass="vaccine";
researchComplete(pass);
//alert(vaccineFinished+"-----"+vaccineEffect);  //these are for debugging
medicineEffect+=medicineEffect*(vaccineEffect);
check2++;
}

if (interferonInc>=interferonFinished&&check3<1)
{
var pass="interferon";
researchComplete(pass);
//alert(interferonFinished+"-----"+interferonEffect);
medicineEffect+=medicineEffect*(interferonEffect);
check3++;
}

if (aegisInc>=aegisFinished&&check4<1)
{
var pass="aegis";
researchComplete(pass);
//alert(medicine+"-----"+aegisEffect);
medicineEffect+=medicineEffect*(aegisEffect);
check4++;
}

//set multipliers

quarantine*=quarantineEffect;
medicine*=medicineEffect;
barrier*=barrierEffect;
totalresource=quarantine+medicine+barrier;
//set new plagueSpread
plagueSpread=Math.floor((Math.random()*7)+5);
turnchange=plagueSpread-totalresource;
//alert(quarantine+"--"+barrier+"----"+turnchange+"----"+plagueSpread);
changeZoneSize(turnchange);
turnNumber++;

if (turnNumber>8)
{
	quarantineEffect=quarantineEffect-1;
	barrierEffect=barrierEffect-1;
}
}



